export function initTabs(){const tabButtons=document.querySelectorAll(".ymc-tabs__nav button");const tabContents=document.querySelectorAll(".ymc-tabs__content");tabButtons.forEach(button=>{button.addEventListener("click",()=>{const target=button.dataset.target;tabButtons.forEach(btn=>btn.classList.remove("active"));tabContents.forEach(content=>content.classList.remove("active"));button.classList.add("active");document.getElementById(target).classList.add("active")})})}