import{YMC_CONTAINER}from"../core/constants.js";export function setPreloader(target){if(undefined===target){YMC_CONTAINER.insertAdjacentHTML("afterbegin",`<div class="preloader"><img src="${YMC_PATH}/assets/images/preloader.svg" alt="preloader"></div>
                    <div class="overlay"></div>`)}else{YMC_CONTAINER.querySelector(target).insertAdjacentHTML("afterbegin",`<div class="preloader preloader--is-local"><img src="${YMC_PATH}/assets/images/preloader.svg" alt="preloader"></div>
                    <div class="overlay overlay--is-local"></div>`)}}export function removePreloader(){YMC_CONTAINER.querySelector(".preloader").remove();YMC_CONTAINER.querySelector(".overlay").remove()}